tinymce.PluginManager.add('embed', function (editor, url) {
    // Add a button that opens a window
    editor.addButton('embed', {
        text: 'embed </>',
        icon: false,
        onclick: function () {
            // Open window
            editor.windowManager.open({
                width: 800,
                height: 500,
                title: 'Embed code plugin',
                body: [
                    { type: 'textbox', name: 'code', label: 'Html code', size: 3000,multiline:true,style: { width: '100%'},rows:"20", cols:"50" }
                ],
                onsubmit: function (e) {
                    // Insert content when the window form is submitted
                    editor.insertContent(e.data.code);
                }
            });
        }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('embed', {
        text: 'Embed code plugin',
        context: 'insert',
        onclick: function () {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'TinyMCE site',
                url: 'http://www.tinymce.com',
                width: 800,
                height: 600,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }]
            });
        }
    });
});